function setCommentsTheme(e){const t={type:"set-theme",theme:e?"github-dark":"github-light"};console.log(t.theme);var n=document.querySelector("iframe");n.contentWindow.postMessage(t,"https://utteranc.es")}let preferDark=window.matchMedia("(prefers-color-scheme: dark)");preferDark.addEventListener("change",e=>{setCommentsTheme(e.matches)}),addEventListener("message",e=>{if(e.origin!=="https://utteranc.es")return;setCommentsTheme(preferDark.matches)})